	INSERT INTO #aunit1 (id, type, name)
		SELECT a.id, ''Звенья предприятия'', b.n_depart FROM ['+@p_base+'_aunit] a left join ['+@p_base+'_departs] b on a.id_item=b.id where a.id_type=1;	
