CREATE PROCEDURE [dbo].[GetBarcodeByIdArchive] 
	@p_base nvarchar(254), -- Имя текущей базы данных
	@p_id_archive int = 0, -- id архива
	@p_id_docversion int = 0 -- id версии
AS
BEGIN
	SET NOCOUNT ON;
	
	DECLARE @ret AS NVARCHAR(MAX) =''
	DECLARE @sql AS NVARCHAR(MAX) =''
	IF (@p_id_archive > 0) --Если передан id архива, берем путь основной версии
		SET @sql = N'SELECT @ret = barcode FROM ['+@p_base+'_docversion] WHERE id_archive='+ cast(@p_id_archive as nvarchar)+' AND main = 1'
	ELSE -- Получаем путь для конкретной версии
		SET @sql = N'SELECT @ret = barcode FROM ['+@p_base+'_docversion] WHERE id='+ cast(@p_id_docversion as nvarchar)
	EXEC sp_executesql 	@sql, N'@ret nvarchar(MAX) OUTPUT', @ret = @ret OUTPUT
	IF (@ret='' OR @ret='0') SET @ret='file'
	select @ret
END



