CREATE PROCEDURE [dbo].[GetChildDoctree] 
	@p_base nvarchar(254), -- Имя текущей базы данных
	@p_childLevel nvarchar(254),
	@p_id_parent nvarchar(254)
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @sql AS NVARCHAR(MAX) =''	

	CREATE TABLE #doctree_tmp (id int,name NVARCHAR(254), id_parent int)
	SET @sql ='INSERT #doctree_tmp select id,treetext,id_parent from dbo.'+@p_base+'_doctree'
	EXEC sp_executesql 	@sql;
	SET @sql ='INSERT #doctree_tmp select id+100000,name, id_doctree from dbo.'+@p_base+'_docname'
	EXEC sp_executesql 	@sql;
	--select * from #doctree_tmp
	
	--SET @sql = 'INSERT INTO #tmp SELECT id,[Name],id_parent from dbo.'+@p_base+'_structur where id in (select id_structur1 from dbo.'+@p_base+'_archive  group by id_structur1) or id in (select id_structur2 from dbo.'+@p_base+'_archive  group by id_structur2)';
	SET @sql ='
	SELECT
		ID, 
		Name, 
		id_parent,
		'+@p_childLevel+' AS tree_level, 
		'+@p_id_parent+' AS tree_parent,
		0 AS tree_expanded,
		CASE WHEN
			(SELECT COUNT(*) AS Expr1
				FROM            
					#doctree_tmp
				WHERE        
					(id_parent = A.ID)
			) = 0 THEN 1 ELSE 0 END AS tree_leaf                                        
		FROM            
		#doctree_tmp AS A
		WHERE
		A.id_parent = '+@p_id_parent+
		' ORDER BY a.name';
	EXEC sp_executesql 	@sql;
END





