CREATE PROCEDURE [dbo].[GetDocnameList] 
(
	@p_base nvarchar(254), -- Имя текущей базы данных
	@p_doctype nvarchar(254) = 'acc' -- Тип документа
)
AS
BEGIN
	DECLARE @p_id_parent AS int = 0
	DECLARE @sql AS NVARCHAR(MAX) =''

	SET @p_id_parent =  
	CASE  @p_doctype
		WHEN 'acc' THEN 7
		WHEN 'ord' THEN 2
		WHEN 'dog' THEN 15
		WHEN 'oth' THEN 56
		WHEN 'empl' THEN 50
		WHEN 'ohs' THEN 60
		WHEN 'tech' THEN 11
		ELSE -1
	END

	CREATE TABLE #ChDoctree (child int)
	CREATE TABLE #ChDocname (child int, name NVARCHAR(MAX))
	INSERT #ChDoctree VALUES(@p_id_parent)
	SET @sql ='
	WHILE @@ROWCOUNT > 0
	BEGIN
		INSERT #ChDoctree
			SELECT a.id
			FROM '+@p_base+'_doctree a JOIN #ChDoctree c ON a.id_parent = c.child
			WHERE NOT EXISTS(SELECT 1 FROM #ChDoctree WHERE child = a.id)
	END 
	INSERT #ChDocname
		SELECT id, name FROM '+@p_base+'_docname WHERE id_doctree IN (SELECT child FROM #ChDoctree)'
	EXEC sp_executesql 	@sql
	
	SET @sql = N'SELECT a.child as id, a.name FROM #ChDocname a ORDER BY a.name'
	EXEC sp_executesql 	@sql
END


