	b.param_131 as id_correct, a.prim, c.param_104 as id_104, a.barcode, a.main, d.short_name as id_user_t, e.param_138 as id_138,
	(CASE 
			WHEN a.file_size/1024.0/1024.0= 0 THEN null 
