	@p_id_docversion int = 0, -- id версии
	@p_hiden nvarchar(3) = '0'  -- 1-Возвращаем путь к файлу , 0-нет
AS
BEGIN
	SET NOCOUNT ON;
	
	DECLARE @ret AS NVARCHAR(MAX) =''
	DECLARE @hiden AS NVARCHAR(MAX) ='0'
	DECLARE @sql AS NVARCHAR(MAX) =''
	IF (@p_id_archive > 0) -- Если передан id архива, берем путь основной версии
	BEGIN
		SET @sql = N'SELECT @hiden = hiden FROM ['+@p_base+'_archive] WHERE id='+ cast(@p_id_archive as nvarchar)
