		SET @sql = N'SELECT @ret = path FROM ['+@p_base+'_docversion] WHERE id_archive='+ cast(@p_id_archive as nvarchar)+' AND main = 1'
	END
	ELSE -- Получаем путь для конкретной версии
	BEGIN
		SET @sql = N'SELECT @hiden = b.hiden FROM ['+@p_base+'_docversion] a JOIN ['+@p_base+'_archive] b ON a.id_archive=b.id WHERE a.id='+ cast(@p_id_docversion as nvarchar)
