		IF (@p_doctype_tech='1') SET @doctype=@doctype +',11'
	END
	ELSE SET @doctype=@doctype +',' + @p_doctype
		
	
	SET @sql ='
	SELECT
    ID, 
    treetext as name,  
    0 AS tree_level, 
    0 AS tree_expanded,
    CASE WHEN
        (SELECT COUNT(*) AS Expr1
            FROM '+@p_base+'_doctree WHERE        
                (id_parent = A.ID)
        ) = 0 THEN 1 ELSE 0 END AS tree_leaf                                        
    FROM            
    '+@p_base+'_doctree AS A
    WHERE
    '+@doctype+')'	;
	EXEC sp_executesql 	@sql;
END




