-- =============================================
-- Author:		fazl
-- Create date: 11.03.2014
-- Description:	Возвращает детей указанного родителя в нужном порядке
-- =============================================
CREATE PROCEDURE [dbo].[GetStructurChild] 
	@p_base nvarchar(254), -- Имя текущей базы данных
	@p_id_parent nvarchar(254) -- id родителя для выборки
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @sql AS NVARCHAR(MAX) =''	
	DECLARE @added AS int = 1	
	CREATE TABLE #tmp (ID int, name NVARCHAR(MAX), id_parent int);
	SET @sql = 'INSERT INTO #tmp SELECT id,[Name],id_parent from dbo.'+@p_base+'_structur where id_parent>0';
	EXEC sp_executesql 	@sql;
	while @added>0
	begin
		SET @sql = 'insert into #tmp SELECT id,[Name],id_parent from dbo.'+@p_base+'_ where id in (select id_parent from #tmp) and id not in (select id from #tmp)'
		EXEC sp_executesql 	@sql;
		SET @added= @@ROWCOUNT;
		--select @added;
		--select * from #tmp;
	end

;WITH
	 NumberedRecords(ID, [Name], id_parent, N, L) AS
		(
			SELECT ID, [Name], id_parent, CAST(STR(ROW_NUMBER()OVER(ORDER BY [Name],ID),11) AS VARCHAR(MAX)), Cast('' as VARCHAR(MAX))  
			FROM #tmp
		),
	 Tree(ID, [Name], id_parent, NodePath, lvl) AS
		(
			SELECT ID, [Name], id_parent, N, L FROM NumberedRecords WHERE ID=@p_id_parent
			UNION ALL
			SELECT T.ID, T.[Name] , T.id_parent, Tree.NodePath+T.N, Tree.lvl+'&#8195'
			FROM Tree JOIN NumberedRecords T ON Tree.ID=T.id_parent
		)
	
SELECT ID,[Name],id_parent,lvl,'' as inlist FROM Tree ORDER BY NodePath
OPTION(MAXRECURSION 0);

END


