	b.content As bcon, y.param_135, us.short_name As suser
	From ['+@p_base+'_registry] As a 
		left join ['+@p_base+'_persons] As c on a.id_sender=c.id
		left join ['+@p_base+'_persons] As d on a.id_recipient=d.id
