			FROM '+@p_base+'_structur a JOIN #ChildrenStructur1 c ON a.id_parent = c.child
			WHERE NOT EXISTS(SELECT 1 FROM #ChildrenStructur1 WHERE child = a.id)
	END' 
	EXEC sp_executesql 	@sql, N'@p_base nvarchar(254)',@p_base = @p_base
	SET @where = @where + ' AND (id_structur1 in (SELECT child FROM #ChildrenStructur1))'
END


IF (@p_structur2!='0')
