	INSERT #ChildrenStructur2 VALUES(@p_structur2)
	SET @sql ='
	WHILE @@ROWCOUNT > 0
	BEGIN
		INSERT #ChildrenStructur2
			SELECT a.id
			FROM '+@p_base+'_structur a JOIN #ChildrenStructur2 c ON a.id_parent = c.child
			WHERE NOT EXISTS(SELECT 1 FROM #ChildrenStructur2 WHERE child = a.id)
	END' 
	EXEC sp_executesql 	@sql, N'@p_base nvarchar(254)',@p_base = @p_base
	SET @where = @where + ' AND (id_structur2 in (SELECT child FROM #ChildrenStructur2))'
