		select a.id_archive from (SELECT count(*) as cnt,id_archive FROM [dbo].['+@p_base+'_docversion] group by id_archive) a where a.cnt'+@p_dvcount
	IF (@p_dvcount='=0') SET @sql ='INSERT #ArchiveDvcount	select a.id from [dbo].['+@p_base+'_archive] a where a.id not in (SELECT id_archive FROM [dbo].['+@p_base+'_docversion])'
	
