CREATE TABLE #tempo(row_num int identity(1, 1) NOT NULL,id int  NOT NULL);

SET @sql='
INSERT INTO #tempo (id)
SELECT TOP ('+Cast((@p_offset + @p_limit) as varchar)+') a.id
FROM (SELECT a.* FROM ['+@p_base+'_archive] a  '+ @where +' ) a '
SET @sql = @sql + 
CASE @p_order_by
	WHEN 'id_docname'		THEN ' Join ['+@p_base+'_docname]		f	On	a.id_docname=f.id  ORDER BY f.name '+@p_order_by_dir
	WHEN 'id_frm2'			THEN ' Left Join ['+@p_base+'_frm]			e	On	a.id_frm2=e.id  ORDER BY e.name '+@p_order_by_dir
	WHEN 'id_doctype'		THEN ' Join ['+@p_base+'_util]			b	On	a.id_doctype=b.id_133  ORDER BY b.param_133 '+@p_order_by_dir
	WHEN 'id_user'			THEN ' Join ['+@p_base+'_users]			h	On	a.id_user=h.id  ORDER BY h.short_name '+@p_order_by_dir
	WHEN 'id_prj_code'      THEN ' Join ['+@p_base+'_prj_code]		pc	On	a.id_prj_code = pc.id  ORDER BY pc.name '+@p_order_by_dir
	WHEN 'id_build_place'	THEN ' Join ['+@p_base+'_build_place]	bp	On	a.id_build_place=bp.id  ORDER BY bp.place '+@p_order_by_dir
	WHEN 'id_perf' 			THEN ' Join ['+@p_base+'_persons]		pf	On	a.id_perf=pf.id  ORDER BY pf.short_name '+@p_order_by_dir
	WHEN 'id_structur1'		THEN ' Join ['+@p_base+'_structur]		st1	On	a.id_structur1=st1.id  ORDER BY st1.name '+@p_order_by_dir
	WHEN 'id_structur2'		THEN ' Join ['+@p_base+'_structur]		st2	On	a.id_structur2=st2.id  ORDER BY st2.name '+@p_order_by_dir
	WHEN 'id_137'			THEN ' Join ['+@p_base+'_util]			st	On	a.id_137=st.id_137  ORDER BY st.param_137 '+@p_order_by_dir
	WHEN 'barcode'			THEN ' Join ['+@p_base+'_docversion]	dv1	On	a.id=dv1.id_archive and dv1.main=1  ORDER BY dv1.barcode '+@p_order_by_dir
	WHEN 'id_138'			THEN ' Join ['+@p_base+'_docversion]	dv2	On	a.id=dv2.id_archive and dv2.main=1  ORDER BY dv2.id_138 '+@p_order_by_dir
	WHEN 'file_size'		THEN ' Join ['+@p_base+'_docversion]	dv3	On	a.id=dv3.id_archive and dv3.main=1  ORDER BY dv3.file_size '+@p_order_by_dir
	WHEN 'id_user_t'		THEN ' Join ['+@p_base+'_docversion]	dv4	On	a.id=dv4.id_archive and dv4.main=1 Join ['+@p_base+'_persons]  prs	On	dv4.id_user_t=prs.id ORDER BY prs.short_name '+@p_order_by_dir
