CREATE PROCEDURE [dbo].[SelectRegistry] 
(
	@p_base nvarchar(254), -- Имя текущей базы данных
	@p_where nvarchar(254) = '(13=13)', -- Условие которое передает грид
	@p_barcode nvarchar(30) = '0', -- Штрих код
	@p_id_archive nvarchar(254) = '0', -- Тип документа
	@p_id_frm2 nvarchar(254) = '0', -- Тип документа
	@p_order_by nvarchar(254) = 'id', -- Поле для сортировки
	@p_order_by_dir nvarchar(254) = ' ASC', -- Направление сортировки
	@p_offset int = 1, -- (Смещение) Для постраничного просмотра 
	@p_limit int = 10 -- (Количество записей) Для постраничного просмотра
)
AS
BEGIN

DECLARE @sql AS NVARCHAR(MAX) =''
DECLARE @order AS NVARCHAR(MAX) = ' ORDER BY ' + @p_order_by + ' ' + @p_order_by_dir
DECLARE @where AS NVARCHAR(MAX) = ' WHERE ' + @p_where 
DECLARE @ret AS int = 0
DECLARE @id AS int = 0

CREATE TABLE #tempo(row_num int identity(1, 1) NOT NULL,id int  NOT NULL);


CREATE TABLE #aunit(id int  NOT NULL,name nvarchar(max));
SET @sql='
	INSERT INTO #aunit (id,name)
		SELECT a.id, b.n_depart FROM ['+@p_base+'_aunit] a left join ['+@p_base+'_departs] b on a.id_item=b.id where a.id_type=1;	
	INSERT INTO #aunit (id,name)
		SELECT a.id, b.name FROM ['+@p_base+'_aunit] a left join ['+@p_base+'_frm] b on a.id_item=b.id where a.id_type=2;
	INSERT INTO #aunit (id,name)
		SELECT a.id, b.short_name FROM ['+@p_base+'_aunit] a left join ['+@p_base+'_persons] b on a.id_item=b.id where a.id_type=3;'
EXEC sp_executesql 	@sql	

IF (@p_barcode!='0')
BEGIN
	CREATE TABLE #RegistryBarcode (child int)
	SET @sql ='
	INSERT #RegistryBarcode
		SELECT a.id_registry
		FROM '+@p_base+'_registry_arc a 
		JOIN '+@p_base+'_docversion b ON a.id_docversion=b.id WHERE ' + @p_barcode
	EXEC sp_executesql 	@sql, N'@p_base nvarchar(254)',@p_base = @p_base
	SET @where = @where + ' AND (id in (SELECT child FROM #RegistryBarcode))'
END


IF (@p_id_archive!='0')
BEGIN
	CREATE TABLE #RegistryIdArchive (child int)
	SET @sql ='
	INSERT #RegistryIdArchive
		SELECT a.id_registry
		FROM '+@p_base+'_registry_arc a
		JOIN '+@p_base+'_docversion b ON a.id_docversion=b.id WHERE ' + @p_id_archive
	EXEC sp_executesql 	@sql, N'@p_base nvarchar(254)',@p_base = @p_base
	SET @where = @where + ' AND (id in (SELECT child FROM #RegistryIdArchive))'
END
--select * from #RegistryIdArchive

IF (@p_id_frm2!='0')
BEGIN
	CREATE TABLE #RegistryIdFrm2 (child int)
	SET @sql ='
	INSERT #RegistryIdFrm2
		SELECT a.id_registry
		FROM '+@p_base+'_registry_arc a 
		JOIN '+@p_base+'_docversion b ON a.id_docversion = b.id
		JOIN '+@p_base+'_archive c ON b.id_archive = c.id
		WHERE c.id_frm2=' + @p_id_frm2
	EXEC sp_executesql 	@sql, N'@p_base nvarchar(254)',@p_base = @p_base
	SET @where = @where + ' AND (id in (SELECT child FROM #RegistryIdFrm2))'
END

SET @sql='
INSERT INTO #tempo (id)
SELECT TOP ('+Cast((@p_offset + @p_limit) as varchar)+') a.id
FROM (SELECT a.* FROM ['+@p_base+'_registry] a  '+ @where +' ) a '

SET @sql = @sql + 
CASE @p_order_by
	WHEN 'id_docplace'		THEN ' Join #aunit						f	On	a.id_docplace=f.id  ORDER BY f.name '+@p_order_by_dir
	WHEN 'id_sender'		THEN ' Join ['+@p_base+'_persons]		e	On	a.id_sender=e.id  ORDER BY e.short_name '+@p_order_by_dir
	WHEN 'id_recipient'		THEN ' Join ['+@p_base+'_persons]		b	On	a.id_recipient=b.id  ORDER BY b.short_name '+@p_order_by_dir
	WHEN 'id_sklad1'		THEN ' Join #aunit						h	On	a.id_sklad1=h.id  ORDER BY h.name '+@p_order_by_dir
	WHEN 'id_user'			THEN ' Join ['+@p_base+'_users]			us	On	a.id_user = us.id  ORDER BY us.short_name '+@p_order_by_dir
	WHEN 'id_135'			THEN ' Join ['+@p_base+'_util]			st	On	a.id_135=st.id_135  ORDER BY st.param_135 '+@p_order_by_dir
	WHEN 'id_reg'			THEN ' ORDER BY a.id '+@p_order_by_dir
	ELSE  @order
END
EXEC sp_executesql 	@sql
--select * from #tempo

SET @sql=N'
SELECT  -a.id as id,a.id as id_reg, a.date, a.prim, a.id_archive, us.short_name As id_user, c.short_name as id_sender, 
		d.short_name as id_recipient, r.name as id_sklad1, s.name As id_docplace, u.param_135 as id_135
FROM #tempo t 
	INNER JOIN ['+@p_base+'_registry]	a	ON t.id = a.id 
	LEFT JOIN ['+@p_base+'_users]		us	ON a.id_user = us.id 
	LEFT JOIN ['+@p_base+'_persons]		c	ON a.id_sender=c.id 
	LEFT JOIN ['+@p_base+'_persons]		d	ON a.id_recipient=d.id 
	LEFT JOIN ['+@p_base+'_util]		u	ON a.id_135=u.id_135
	LEFT JOIN #aunit					s	ON a.id_docplace = s.id 
    LEFT JOIN #aunit					r	ON a.id_sklad1 = r.id 
    
WHERE t.row_num BETWEEN '+Cast((@p_offset) as varchar)+' AND '+Cast((@p_offset + @p_limit - 1) as varchar)
IF (@p_order_by='id') SET @sql=@sql+' ORDER BY a.id '+ @p_order_by_dir
EXEC sp_executesql 	@sql

SET @sql = N'SELECT @ret = COUNT(*) FROM ['+@p_base+'_registry] a  '+ @where
EXEC sp_executesql 	@sql, N'@ret int OUTPUT', @ret = @ret OUTPUT

IF OBJECT_ID(N'tempdb..#tempo', N'U') IS NOT NULL drop table #tempo
