-- =============================================
CREATE PROCEDURE [dbo].[UpdateStructurList] 
(
	@p_base nvarchar(254) -- Имя текущей базы данных
)
AS
BEGIN
	DECLARE @sql AS NVARCHAR(MAX) =''	
	SET @sql='DROP INDEX [IX_structur_list_id_parent] ON [dbo].['+@p_base+'_structur_list]'
	EXEC sp_executesql 	@sql
	SET @sql='DROP INDEX [IX_structur_list_id] ON [dbo].['+@p_base+'_structur_list]'
	EXEC sp_executesql 	@sql
	SET @sql='DROP TABLE [dbo].['+@p_base+'_structur_list]'
	EXEC sp_executesql 	@sql
	SET @sql='
	CREATE TABLE [dbo].['+@p_base+'_structur_list](
		[id] [int] NOT NULL,
		[name] [nvarchar](100) NOT NULL,
		[id_parent] [int] NOT NULL,
		[lvl][nvarchar](100) NOT NULL,
		[inlist] [nvarchar](MAX) NOT NULL
	) ON [PRIMARY] TEXTIMAGE_ON [PRIMARY]
	'
	EXEC sp_executesql 	@sql

	SET @sql='CREATE NONCLUSTERED INDEX [IX_structur_list_id] ON [dbo].['+@p_base+'_structur_list](	[id] ASC )WITH (PAD_INDEX = OFF, STATISTICS_NORECOMPUTE = OFF, SORT_IN_TEMPDB = OFF, DROP_EXISTING = OFF, ONLINE = OFF, ALLOW_ROW_LOCKS = ON, ALLOW_PAGE_LOCKS = ON) ON [PRIMARY]'
	EXEC sp_executesql 	@sql
	SET @sql='CREATE NONCLUSTERED INDEX [IX_structur_list_id_parent] ON [dbo].['+@p_base+'_structur_list]([id_parent] ASC)WITH (PAD_INDEX = OFF, STATISTICS_NORECOMPUTE = OFF, SORT_IN_TEMPDB = OFF, DROP_EXISTING = OFF, ONLINE = OFF, ALLOW_ROW_LOCKS = ON, ALLOW_PAGE_LOCKS = ON) ON [PRIMARY]'
	EXEC sp_executesql 	@sql

	CREATE TABLE #AllStructur (ID int, name NVARCHAR(MAX), id_parent int);
	SET @sql = 'INSERT INTO #AllStructur SELECT id,[Name],id_parent from dbo.'+@p_base+'_structur where id in (select id_structur1 from dbo.'+@p_base+'_archive  group by id_structur1) or id in (select id_structur2 from dbo.'+@p_base+'_archive  group by id_structur2)';
	EXEC sp_executesql 	@sql;
	-- Найдем всех родителей
	DECLARE @added AS  int=1
	while @added>0
	begin
		SET @sql = 'insert into #AllStructur SELECT id,[Name],id_parent from dbo.'+@p_base+'_structur where id in (select id_parent from #AllStructur) and id not in (select id from #AllStructur)'
		EXEC sp_executesql 	@sql;
		SET @added= @@ROWCOUNT;
	end

	
	DECLARE @cur_parent AS int =0
	DECLARE @i AS int =0
	DECLARE @n_parents AS int =0
	CREATE TABLE #MainParents (rnum int,id int)
	CREATE TABLE #result (rn int identity not null,ID int, name NVARCHAR(MAX), id_parent int, lvl NVARCHAR(MAX), inlist NVARCHAR(MAX));
	SET @sql = 'INSERT INTO #MainParents SELECT ROW_NUMBER()OVER(ORDER BY [name]), id FROM '+@p_base+'_structur WHERE id_parent=0 order by name'
	EXEC sp_executesql 	@sql

	SET @n_parents=@@ROWCOUNT
	SET @i = 1
	WHILE @i <= @n_parents
	BEGIN
		SELECT @cur_parent = id FROM #MainParents WHERE rnum = @i
		;WITH
		 NumberedRecords(ID, [Name], id_parent, N, L) AS
			(
				SELECT ID, [Name], id_parent, CAST(STR(ROW_NUMBER()OVER(ORDER BY [Name],ID),11) AS VARCHAR(MAX)), Cast('' as VARCHAR(MAX))  
				FROM #AllStructur
			),
		 Tree(ID, [Name], id_parent, NodePath, lvl) AS
			(
				SELECT ID, [Name], id_parent, N, L FROM NumberedRecords WHERE ID=@cur_parent
				UNION ALL
				SELECT T.ID, T.[Name] , T.id_parent, Tree.NodePath+T.N, Tree.lvl+'&#8195'
				FROM Tree JOIN NumberedRecords T ON Tree.ID=T.id_parent
			)	
		insert into #result (ID, name, id_parent,lvl) select ID,[Name],id_parent,lvl as inlist FROM Tree ORDER BY NodePath OPTION(MAXRECURSION 0);
		SET @i = @i + 1
	END
	CREATE TABLE #ChStructur (child int)
	select * from #result
	SET @n_parents=@@ROWCOUNT
	SET @i = 1
	DECLARE @cur_id AS int =0
	DECLARE @inlist AS NVARCHAR(MAX)
	WHILE @i <= @n_parents
	BEGIN
		select @cur_id=id from #result where rn=@i
		TRUNCATE TABLE #ChStructur
		INSERT #ChStructur VALUES(@cur_id)
		SET @sql ='
		WHILE @@ROWCOUNT > 0
		BEGIN
			INSERT #ChStructur
				SELECT a.id
				FROM '+@p_base+'_structur a JOIN #ChStructur c ON a.id_parent = c.child
				WHERE NOT EXISTS(SELECT 1 FROM #ChStructur WHERE child = a.id)
		END'
		EXEC sp_executesql 	@sql
		SET @inlist='-1'
		SELECT @inlist=@inlist+','+CAST(a.child as varchar(100)) FROM #ChStructur a 
		Update #result SET inlist = @inlist WHERE rn=@i
		SET @i = @i + 1
	END

	SET @sql ='INSERT into '+@p_base+'_structur_list select ID, name, id_parent, lvl, inlist from #result'
	EXEC sp_executesql 	@sql

	drop table #MainParents;
	drop table #AllStructur;
	drop table #result;
	drop table #ChStructur;
END




